document.addEventListener("DOMContentLoaded",()=>{(()=>{const getDropDown=document.querySelectorAll("[data-dropdown]");for(let i=0;i<getDropDown.length;i++)getDropDown[i].addEventListener("click",(function(e){!1===this.classList.contains("active")?this.classList.add("active"):!0===this.classList.contains("active")&&this.classList.remove("active"),e.stopPropagation()}));document.addEventListener("click",(function(e){for(let i=0;i<getDropDown.length;i++)e.target!==getDropDown[i].hasAttribute("menu")&&getDropDown[i].classList.remove("active").hasAttribute("menu")}))})(),(()=>{const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i++)intLinks[i].addEventListener("click",(function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))})()});
