document.addEventListener("DOMContentLoaded",()=>{(()=>{const getDropDown=document.querySelectorAll("[data-dropdown]"),getMenu=document.querySelectorAll('[data-dropdown="menu"]'),getToolTip=document.querySelectorAll('[data-dropdown="tooltip"]');for(let i=0;i<getDropDown.length;i++)getDropDown[i].addEventListener("click",(function(e){!1===this.classList.contains("active")?this.classList.add("active"):!0===this.classList.contains("active")&&this.classList.remove("active"),e.stopPropagation()}));document.addEventListener("click",(function(e){for(let i=0;i<getMenu.length;i++)e.target!==getMenu[i]&&getMenu[i].classList.remove("active");for(let i=0;i<getToolTip.length;i++)e.target!==getToolTip[i]&&getToolTip[i].classList.remove("active")}))})(),(()=>{const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i++)intLinks[i].addEventListener("click",(function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})}))})()});
