document.addEventListener("DOMContentLoaded",function(){!function(){const t=document.querySelectorAll("[data-dropdown]");for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(t){!1===this.classList.contains("active")?this.classList.add("active"):!0===this.classList.contains("active")&&this.classList.remove("active"),t.stopPropagation()});document.addEventListener("click",function(e){for(let n=0;n<t.length;n++)e.target!==t[n].hasAttribute("menu")&&t[n].classList.remove("active").hasAttribute("menu")})}(),function(){const t=document.querySelectorAll("a[href^='#']");for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(t){t.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}()});
