document.addEventListener("DOMContentLoaded",function(){var c=document.querySelectorAll("a[href^='#']"),b=document.querySelectorAll(".dropdown"),d=b.length,a;for(a=0;a<d;a++)b[a].addEventListener("click",function(a){a.stopPropagation();this.classList.contains("drop")?this.classList.remove("drop"):this.classList.add("drop")});document.addEventListener("click",function(e){if(e.target!=b)for(a=0;a<d;a++)b[a].classList.remove("drop")});for(a=0;a<c.length;a++)c[a].addEventListener("click",function(a){a.preventDefault();
document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});
